$local = "$env:USERPROFILE\.local\bin"; if (-not (Test-Path -Path $local)) { New-Item -ItemType Directory -Path $local }; $cbdep = "$local\cbdep.exe"; if (-not (Test-Path -Path $cbdep)) { $uv = "$local\uv.exe"; if (-not (Test-Path -Path $uv)) { [Net.ServicePointManager]::SecurityProtocol = "Tls12, Ssl3"; irm https://astral.sh/uv/install.ps1 | iex }; Start-Process -FilePath $uv -ArgumentList "tool install --force --quiet cbdep" -NoNewWindow -Wait; };